using Conda

env = @__DIR__
Conda.add_channel("plotly", env)
Conda.add("plotly-orca", env)
Conda.update(env)

if Sys.isunix()
    bindir = "bin"
else # assuming Windows
    bindir = "orca_app"
end

open("paths.jl", "w") do f
    orca_cmd = joinpath(@__DIR__, bindir, "orca")
    println(f, "# This file is automatically generated by build.jl DO NOT EDIT")
    println(f, "const orca_cmd = $(repr(orca_cmd))")
end
